import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';


export default function Signup() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [errors, setErrors] = useState({});
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData({ ...formData, [id]: value });
  };

  const validate = () => {
    let tempErrors = {};
    if (!formData.name) tempErrors.name = "Name is required";
    if (!formData.email) tempErrors.email = "Email is required";
    if (!formData.password) tempErrors.password = "Password is required";
    if (formData.password !== formData.confirmPassword) tempErrors.confirmPassword = "Passwords do not match";
    setErrors(tempErrors);
    return Object.keys(tempErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validate()) {
      try {
        const response = await axios.post('https://66829f814102471fa4c79bd6.mockapi.io/info', {
          name: formData.name,
          email: formData.email,
          password: formData.password
        });
        setMessage('Account created successfully!');
        setFormData({
          name: '',
          email: '',
          password: '',
          confirmPassword: ''
        });
        setErrors({});
      } catch (error) {
        setMessage('Failed to create account');
      }
    }
  };

  return (
    <>
      <div className="flex flex-col bg-black lg:flex-row-reverse lg:p-2 lg:mx-9 lg:mt-4">
        <div className="flex flex-col min-h-screen">
          <div className="container flex-1 flex flex-col items-center max-w-lg">
            <form className="flex flex-col p-6 w-[500px] rounded-2xl" onSubmit={handleSubmit} style={{
                backgroundImage:
                  "url('https://img.freepik.com/free-photo/starry-night-sky_1048-11828.jpg')",
              }}>
              <h1 className="text-center text-5xl mb-6 text-neutral-200">Sign Up</h1>
              
              {message && <div className="mb-4 text-center text-red-500">{message}</div>}

              <input
                id="name"
                type="text"
                className="w-auto mb-8 mt-6 mx-8 rounded-lg focus:text-gray-200 placeholder-gray-200 border-gray-200 border-t-transparent bg-transparent focus:outline-none focus:ring-transparent focus:border-gray-200 focus:placeholder-transparent"
                placeholder="Name"
                value={formData.name}
                onChange={handleChange}
              />
              {errors.name && <span className="text-red-500 mx-8">{errors.name}</span>}

              <input
                id="email"
                type="email"
                className="w-auto mb-8 mt-6 mx-8 rounded-lg shadow-none text-gray-200 placeholder-gray-200 border-gray-200 border-t-transparent bg-transparent focus:outline-none focus:ring-transparent focus:border-gray-200 focus:placeholder-transparent"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
              />
              {errors.email && <span className="text-red-500 mx-8">{errors.email}</span>}

              <input
                id="password"
                type="password"
                className="text-gray-200 w-auto mb-8 mt-6 mx-8 rounded-lg bg-transparent border-gray-200 border-t-transparent placeholder-gray-200 focus:outline-none focus:ring-transparent focus:border-gray-200 focus:placeholder-transparent"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
              />
              {errors.password && <span className="text-red-500 mx-8">{errors.password}</span>}

              <input
                id="confirmPassword"
                type="password"
                className="w-auto mb-8 mt-6 mx-8 rounded-lg text-gray-200 placeholder-gray-200 border-gray-200 border-t-transparent bg-transparent focus:outline-none focus:ring-transparent focus:border-gray-200 focus:placeholder-transparent"
                placeholder="Confirm Password"
                value={formData.confirmPassword}
                onChange={handleChange}
              />
              {errors.confirmPassword && <span className="text-red-500 mx-8">{errors.confirmPassword}</span>}

              <button
                type="submit"
                className="relative inline-flex items-center justify-center p-0.5 mb-8 mt-6 mx-8 overflow-hidden text-sm font-medium text-neutral-200 rounded-lg group bg-gradient-to-br from-purple-600 to-blue-500 group-hover:from-purple-600 group-hover:to-blue-500 hover:text-white dark:text-white focus:outline-none dark:focus:ring-blue-800"
              >
                <span className="relative text-lg w-full px-5 py-2.5 transition-all ease-in duration-75 bg-black dark:bg-gray-900 rounded-md group-hover:bg-opacity-0">
                  Create Account
                </span>
              </button>

              <div className="inline-flex items-center justify-center w-full relative">
                <hr className="w-64 h-px my-8 bg-neutral-200 border-0" />
                <span className="absolute px-3 font-medium text-neutral-200 bg-black">
                  or
                </span>
              </div>

              <button
                type="button"
                className="text-white bg-[#3b5998] hover:bg-[#3b5998]/90 focus:ring-4 focus:outline-none focus:ring-[#3b5998]/50 font-medium rounded-lg text-sm px-5 py-2.5 text-center inline-flex items-center dark:focus:ring-[#3b5998]/55 mr-2 mb-2"
              >
                <svg
                  className="w-4 h-4 mr-2"
                  aria-hidden="true"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="currentColor"
                  viewBox="0 0 8 19"
                >
                  <path
                    fillRule="evenodd"
                    d="M6.135 3H8V0H6.135a4.147 4.147 0 0 0-4.142 4.142V6H0v3h2v9.938h3V9h2.021l.592-3H5V3.591A.6.6 0 0 1 5.592 3h.543Z"
                    clipRule="evenodd"
                  />
                </svg>
                Sign up with Facebook
              </button>
              <button
                type="button"
                className="text-black bg-white hover:bg-gray-200 focus:ring-4 focus:outline-none focus:ring-gray-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center inline-flex items-center mr-2 mb-2"
              >
                <img
                  className="w-5 h-5 mr-1"
                  src="data:image/png;base64,"

                />
                Sign up with Google
              </button>
              <div className="flex flex-row justify-center gap-4">
                <a className="text-white" href="/login">
                  Forgot password?
                </a>
                <Link to="/login" className='text-white' onClick={() => localStorage.clear()}>
                Log in
                </Link>
              </div>
            </form>
          </div>
         
        </div>
        <form className="bg-black lg:card-body lg:mt-32">
          <img className="w-[100%] lg:w-[80%]" src="data:image/png;base64," alt="" />
        </form>
      </div>
    </>
  );
}

























